import React, { Fragment } from 'react'
import Ratings from './Ratings'


const BeerItem = (beer) => {
  // console.log('beer', beer)
  // console.log(beer.beer.rating)
  const link = beer.beer.link
  return (
    <div className='card card-body mb-3'>
      <div className='row'>
        <div className='col-md-9'>
          <h5>{beer.beer.name}</h5>
          <h5>Style : {beer.beer.style}</h5>
          <h5>ABV : {beer.beer.abv} %</h5>
          <h5>IBU : {beer.beer.ibu}</h5>

          {beer.beer.ratings.map((rating) => {
            return (
              <h5 key={rating.name}>
                {rating.value} from {rating.name} - 
              </h5>
            )
          })}{' '}
          {/* {beer.beer.ratings.map((rating) => (
            <Fragment>
              <Ratings name={rating.name} value={rating.value} />
            </Fragment>
          ))} */}
          <a href={link} target='_blank'>
            <button className='btn btn-link btn-sm' type='submit'>
              Want it!
            </button>
          </a>
        </div>
        <div className='col-md-3' />
      </div>
    </div>
  )
}

export default BeerItem

