import React, { Component, Fragment, useState, useEffect } from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import { useQuery } from '@apollo/react-hooks'
import BeerItem from './BeerItem'
import axios from 'axios'
import Pagination from './Pagination'
import InfiniteScroll from 'react-infinite-scroll-component'
import uniqBy from 'lodash.uniqby'
import chunk from 'lodash.chunk'
// var chunk = require('lodash.chunk')
// var uniqBy = require('lodash.uniqby')
// import InfiniteScroll from 'react-infinite-scroller'

const BEERS_QUERY = gql`
  query BeersQuery {
    beers {
      abv
      available
      brewery
      ibu
      link
      name
      price
      ratings {
        name
        value
      }
      style
    }
  }
`

export const Beers = () => {
  const { loading, error, data } = useQuery(BEERS_QUERY)

  const [beers, setBeers] = useState([])
  const [beersChunk, setbeersChunk] = useState([])
  const [filteredBy, setFilter] = useState('PRICE')
  const [avg, setAvg] = useState({ price: 0, untappd: 0, rateBeer: 0 })

  const { price, untappd, rateBeer } = avg

  const [count, setCount] = useState(0)

  const sortByPrice = (a, b) => {
    let priceA = parseInt(a.price)
    let priceB = parseInt(b.price)

    if (priceA < priceB) {
      return -1
    }
    if (priceA > priceB) {
      return 1
    }
    return 0
  }

  const sortByUntappd = (a, b) => {
    let ratingA = parseFloat(a.ratings[0].value)
    let ratingB = parseFloat(b.ratings[0].value)

    return ratingB - ratingA
  }

  const sortByRatebeer = (a, b) => {
    let ratingA = 0
    let ratingB = 0
    if (a.ratings[1] && b.ratings[1]) {
      ratingA = parseFloat(a.ratings[1].value)
      ratingB = parseFloat(b.ratings[1].value)
    }

    return ratingB - ratingA
  }

  //Without chunk
  useEffect(() => {
    if (loading === false && data) {
      setBeers(uniqBy(data.beers, 'name'))
    }
  }, [loading, data])

  useEffect(() => {
    if (loading === false && data) {
      setbeersChunk(chunk(uniqBy(data.beers, 'name'), 10))
    }
  }, [loading, data])
  console.log(data)
  console.log(beers)

  console.log(beersChunk[0])
  // Calculate AVG
  let len = beers.length
  let sumPrice = 0
  let sumRateBeer = 0
  let sumUntappd = 0
  let lenPrice = 0
  let lenUntapppd = 0
  let lenRateBeer = 0

  // let forUntappd = beers.filter((beer) => beer.ratings[0].value !== undefined)

  for (var i = 0; i < len; i++) {
    if (beers[i].price && beers[i].price !== '0') {
      sumPrice += parseFloat(beers[i].price)
      lenPrice += 1
    }

    if (beers[i].ratings[0] && beers[i].ratings[0].value !== '0') {
      sumUntappd += parseFloat(beers[i].ratings[0].value)
      lenUntapppd += 1
    }
    if (beers[i].ratings[1] && beers[i].ratings[1].value !== '0') {
      sumRateBeer += parseFloat(beers[i].ratings[1].value)
      lenRateBeer += 1
    }
  }

  // console.log('beers', beers)

  // calAvg()
  // console.log('lenUntapppd :', lenUntapppd, 'lenRateBeer : ', lenRateBeer)
  useEffect(() => {
    if (sumUntappd !== 0 && sumPrice !== 0 && sumRateBeer !== 0) {
      setAvg({
        ...avg,
        price: (sumPrice / lenPrice).toFixed(2),
        untappd: (sumUntappd / lenUntapppd).toFixed(2),
        rateBeer: (sumRateBeer / lenRateBeer).toFixed(2),
      })
    }
    // console.log(avg)
  }, [sumPrice, sumUntappd, sumRateBeer])

  // console.log(avg)

  // console.log(beers)

  switch (filteredBy) {
    case 'PRICE':
      beers.sort(sortByPrice)
      break
    case 'UNTAPPD':
      beers.sort(sortByUntappd)
      break
    case 'RATE_BEER':
      beers.sort(sortByRatebeer)
      break
    default:
      break
  }

  if (loading) return <h4>Loading...</h4>
  if (error) console.log(error)

  let loadedBeers = 0
  return (
    <div>
      <div className='cardy text-white bg-light mb-3 sticky-top pb-3'>
        <h1 className='display-4 my-3 filxed-top bg-light ml-4 '>
          Beers Quebec{' '}
        </h1>
        {/* <div className='card-header'></div> */}
        <div className='card-body'>
          {/* <h4 className='card-title'>Primary card title</h4> */}

          <form className='form-inline '>
            <button
              className={
                filteredBy === 'PRICE' ? 'btn btn-primary' : 'btn btn-secondary'
              }
              type='button'
              onClick={() => {
                beers.sort(sortByUntappd)
                setBeers(beers)
                setFilter('PRICE')
                window.scrollTo(0, 0)
              }}
            >
              By Price
            </button>
            <button
              className={
                filteredBy === 'UNTAPPD'
                  ? 'btn btn-primary '
                  : 'btn btn-secondary'
              }
              type='button'
              onClick={() => {
                beers.sort(sortByUntappd)
                setBeers(beers)
                setFilter('UNTAPPD')
                window.scrollTo(0, 0)
              }}
            >
              By Untappd
            </button>
            <button
              className={
                filteredBy === 'RATE_BEER'
                  ? 'btn btn-primary'
                  : 'btn btn-secondary'
              }
              type='button'
              onClick={() => {
                beers.sort(sortByUntappd)
                setBeers(beers)
                setFilter('RATE_BEER')
                window.scrollTo(0, 0)
              }}
            >
              By Ratebeer{' '}
            </button>
          </form>
          <div className='avg sticky-top col-sm-4 mt-3'>
            <h5 className='avgPart  '>Avg. Price : $ {avg.price}</h5>
            <h5 className='  '> Avg. Untappd : {avg.untappd} / 5</h5>
            <h5 className='  '> Avg. Ratebeer : {avg.rateBeer} / 5</h5>
          </div>
        </div>
      </div>

      {/* <nav className='nav sticky-top col-sm-4'></nav> */}

      {beers.map((beer) => {
        if (loadedBeers < 11) {
          loadedBeers += 1

          console.log('loadedBeers', loadedBeers)

          return <BeerItem key={beer.name} beer={beer} />
        } else {
          loadedBeers += 1

          window.addEventListener('scroll', function () {
            if (
              window.scrollY + window.innerHeight + 100 >=
              document.documentElement.scrollHeight
            ) {
              console.log('More loading')
              console.log('loadedBeers', loadedBeers)

              {
                /* fetchRandomImages(5) */
              }
            }
          })
        }
      })}
    </div>
  )
}

