import React, { Component, Fragment, useState, useEffect } from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import { useQuery } from '@apollo/react-hooks'
import BeerItem from './BeerItem'
import axios from 'axios'
import Pagination from './Pagination'
import InfiniteScroll from 'react-infinite-scroll-component'
import uniqBy from 'lodash.uniqby'
// var uniqBy = require('lodash.uniqby')
// import InfiniteScroll from 'react-infinite-scroller'

const BEERS_QUERY = gql`
  query BeersQuery {
    beers {
      abv
      available
      brewery
      ibu
      link
      name
      price
      ratings {
        name
        value
      }
      style
    }
  }
`

export const Beers = () => {
  const { loading, error, data } = useQuery(BEERS_QUERY)

  const [beers, setBeers] = useState([])
  const [filteredBy, setFilter] = useState('PRICE')

  const sortByPrice = (a, b) => {
    let priceA = parseInt(a.price)
    let priceB = parseInt(b.price)

    if (priceA < priceB) {
      return -1
    }
    if (priceA > priceB) {
      return 1
    }

    return 0
  }

  const sortByUntappd = (a, b) => {
    let ratingA = parseFloat(a.ratings[0].value)
    let ratingB = parseFloat(b.ratings[0].value)

    return ratingB - ratingA
  }

  const sortByRatebeer = (a, b) => {
    let ratingA = 0
    let ratingB = 0
    if (a.ratings[1] && b.ratings[1]) {
      ratingA = parseFloat(a.ratings[1].value)
      ratingB = parseFloat(b.ratings[1].value)
    }

    return ratingB - ratingA
  }
  // console.log(data)

  useEffect(() => {
    if (loading === false && data) {
      setBeers(uniqBy(data.beers, 'name'))
    }
  }, [loading, data])
  // console.log(beers)

  switch (filteredBy) {
    case 'PRICE':
      beers.sort(sortByPrice)
      break
    case 'UNTAPPD':
      beers.sort(sortByUntappd)
      break
    case 'RATE_BEER':
      beers.sort(sortByRatebeer)
      break
    default:
      break
  }

  if (loading) return <h4>Loading...</h4>
  if (error) console.log(error)

  return (
    <Fragment>
      <h1 className='display-4 my-3'>Beers du Quebec </h1>
      <nav className='nav'>
        <form className='form-inline'>
          <button
            className={
              filteredBy === 'PRICE' ? 'btn btn-primary' : 'btn btn-secondary'
            }
            type='button'
            onClick={() => {
              beers.sort(sortByUntappd)
              setBeers(beers)
              setFilter('PRICE')
            }}
          >
            By Price
          </button>
          <button
            className={
              filteredBy === 'UNTAPPD' ? 'btn btn-primary' : 'btn btn-secondary'
            }
            type='button'
            onClick={() => {
              beers.sort(sortByUntappd)
              setBeers(beers)
              setFilter('UNTAPPD')
            }}
          >
            By Untappd
          </button>
          <button
            className={
              filteredBy === 'RATE_BEER'
                ? 'btn btn-primary'
                : 'btn btn-secondary'
            }
            type='button'
            onClick={() => {
              beers.sort(sortByUntappd)
              setBeers(beers)
              setFilter('RATE_BEER')
            }}
          >
            By Ratebeer
          </button>
        </form>
      </nav>
      <h5 mt-1>Avg. </h5>

      <Fragment>
        {beers.map((beer) => (
          <BeerItem key={beer.name} beer={beer} />
        ))}
      </Fragment>
    </Fragment>
  )
}
