import React from 'react'
import InfiniteScroll from 'react-infinite-scroll-component'

const BeerItem = (beer) => {
  // console.log('beer', beer)
  // console.log(beer.beer.rating)
  const link = beer.beer.link

  //   const toFixe = (num, fixed) => {
  //     var re = new RegExp('^-?\\d+(?:\.\\d{0,' + (fixed || -10) + '})?');
  //     return num.toString().match(re)[0];
  // }
  // const abv = toFixe(beer.beer.abv,-100 )

  return (
    <div className='album'>
      <div className='card card-body mb-3 col-md-6 '>
        <div className=''>
          <div className='row'>
            <div className='col-md-12'>
              <h3 className='liner'>{beer.beer.name}</h3>
              {beer.beer.style ? (
                <h5 className='liner'>{beer.beer.style}</h5>
              ) : (
                <h5>'N/A'</h5>
              )}
              <h5 className='liner'>ABV : {beer.beer.abv} %</h5>
              <h5 className='liner'>IBU : {beer.beer.ibu}</h5>
              <h5 className='liner'>By {beer.beer.brewery}</h5>
              <h5 className='liner'>$ {beer.beer.price} </h5>
              <hr></hr>
              <h4>Ratings</h4>
              {beer.beer.ratings.map((rating, index, arr) => {
                if (arr.length === 2) {
                  return (
                    <h5 className='liner' key={rating.name}>
                      {rating.value} from {rating.name}
                    </h5>
                  )
                } else if (arr.length === 1 && rating.name === 'untappd') {
                  return (
                    <div>
                      <h5 className='liner' key={rating.name}>
                        {rating.value} from {rating.name}
                      </h5>
                      <h5 className='liner' key='ff'>
                        N/A from RateBeer
                      </h5>
                    </div>
                  )
                } else if (arr.length === 1 && rating.name === 'RateBeer') {
                  return (
                    <div>
                      <h5 className='liner' key={rating.name}>
                        {rating.value} from {rating.name}
                      </h5>
                      <h5 className='liner' key='ff'>
                        N/A from untappd
                      </h5>
                    </div>
                  )
                }

                {
                  /* if (rating.name === 'RateBeer') {
                  return (
                    <h5 className='liner' key={rating.name}>
                      {rating.value} from {rating.name}
                    </h5>
                  )
                } else if (rating.name === 'untappd') {
                  return (
                    <h5 className='liner' key={rating.name}>
                      {rating.value} from {rating.name}
                    </h5>
                  )
                } else if (
                  rating.name === 'untappd' &&
                  rating.name !== 'RateBeer'
                ) {
                  return <h5>N/A</h5>
                } */
                }

                {
                  /* 
                if (rating.name === 'untappd') {
                  return (
                    <h5 className='liner' key={rating.name}>
                      {rating.value} from {rating.name}
                    </h5>
                  )
                } else if (rating.name === 'RateBeer') {
                  return (
                    <h5 className='liner' key={rating.name}>
                      {rating.value} from {rating.name}
                    </h5>
                  )
                } else if (
                  rating.name === 'untappd' &&
                  rating.name !== 'RateBeer'
                ) {
                  return <h5>N/A</h5>
                } */
                }
              })}{' '}
              <hr></hr>
              {/* {beer.beer.ratings.map((rating) => (
            <Fragment>
              <Ratings name={rating.name} value={rating.value} />
            </Fragment>
          ))} */}
              <a href={link} target='_blank' rel='noopener noreferrer'>
                <button className='btn btn-link btn-sm ' type='submit'>
                  <h5 className='wantit'> WANT IT!</h5>
                </button>
              </a>
            </div>
            <div className='col-md-3' />
          </div>
        </div>
      </div>
    </div>
  )
}

export default BeerItem

