import React, { Fragment } from 'react'



const BeerItem = (beer) => {
  // console.log('beer', beer)
  // console.log(beer.beer.rating)
  const link = beer.beer.link

  const toFixe = (num, fixed) => {
    var re = new RegExp('^-?\\d+(?:\.\\d{0,' + (fixed || -1) + '})?');
    return num.toString().match(re)[0];
}
toFixe(beer.beer.abv,-1 )

  return (
    <div className='card card-body mb-3'>
      <div className='row'>
        <div className='col-md-9'>
          <h1>{beer.beer.name}</h1>
          <h4>{beer.beer.style}</h4>
          <h5>ABV : {beer.beer.abv} %</h5>
          <h5>IBU : {beer.beer.ibu}</h5>
          <h5>$ {beer.beer.price} </h5>
          <hr></hr>
          <h3>Ratings</h3>
          {beer.beer.ratings.map((rating) => {
            return (
              <h5 key={rating.name}>
                {rating.value} from {rating.name}
              </h5>
            )
          })}{' '}
          <hr></hr>
          {/* {beer.beer.ratings.map((rating) => (
            <Fragment>
              <Ratings name={rating.name} value={rating.value} />
            </Fragment>
          ))} */}
          <a href={link} target='_blank' rel="noopener noreferrer">
            <button className='btn btn-link btn-sm' type='submit'>
            <h5> WANT IT!</h5> 
            </button>
          </a>
        </div>
        <div className='col-md-3' />
      </div>
    </div>
  )
}

export default BeerItem

