import React, { Component, Fragment, useState, useEffect } from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import BeerItem from './BeerItem'
import axios from 'axios'
import Pagination from './Pagination'
import InfiniteScroll from 'react-infinite-scroll-component'

const BEERS_QUERY = gql`
  query BeersQuery {
    beers {
      abv
      available
      brewery
      ibu
      link
      name
      price
      ratings {
        name
        value
      }
      style
    }
  }
`

// 'https://jsonplaceholder.typicode.com/posts'
// const LINKS_PER_PAGE = 5

const Beers = () => {
  return (
    <Fragment>
      <h1 className='display-4 my-3'>Beers du Quebec </h1>

      <Query query={BEERS_QUERY}>
        {({ loading, error, data, fetchMore }) => {
          if (loading) return <h4>Loading...</h4>
          if (error) console.log(error)
          console.log('data :', data)
          return (
            <Fragment>
              {data.beers.map((beer) => (
                <BeerItem key={beer.name} beer={beer} />
              ))}
            </Fragment>
          )
        }}
      </Query>
    </Fragment>
  )
}

const MoreCommentsQuery = gql`
  query MoreComments($cursor: String) {
    moreComments(cursor: $cursor) {
      cursor
      comments {
        author
        text
      }
    }
  }
`;

const CommentsWithData = () => (
  <Query query={CommentsQuery}>
    {({ data: { comments, cursor }, loading, fetchMore }) => (
      <Comments
        entries={comments || []}
        onLoadMore={() =>
          fetchMore({
            // note this is a different query than the one used in the
            // Query component
            query: MoreCommentsQuery,
            variables: { cursor: cursor },
            updateQuery: (previousResult, { fetchMoreResult }) => {
              const previousEntry = previousResult.entry;
              const newComments = fetchMoreResult.moreComments.comments;
              const newCursor = fetchMoreResult.moreComments.cursor;

              return {
                // By returning `cursor` here, we update the `fetchMore` function
                // to the new cursor.
                cursor: newCursor,
                entry: {
                  // Put the new comments in the front of the list
                  comments: [...newComments, ...previousEntry.comments]
                },
                __typename: previousEntry.__typename
              };
            }
          })
        }
      />
    )}
  </Query>
);

