import React, { Component, Fragment, useState, useEffect } from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import { useQuery } from '@apollo/react-hooks'
import BeerItem from './BeerItem'
import axios from 'axios'
import Pagination from './Pagination'
import InfiniteScroll from 'react-infinite-scroll-component'
import uniqBy from 'lodash.uniqby'
import chunk from 'lodash.chunk'
// var chunk = require('lodash.chunk')
// var uniqBy = require('lodash.uniqby')
// import InfiniteScroll from 'react-infinite-scroller'

const BEERS_QUERY = gql`
  query BeersQuery {
    beers {
      abv
      available
      brewery
      ibu
      link
      name
      price
      ratings {
        name
        value
      }
      style
    }
  }
`

export const Beers = () => {
  const { loading, error, data } = useQuery(BEERS_QUERY)

  const [allBeers, setAllBeers] = useState([])
  const [beers, setBeers] = useState([])
  const [beersChunk, setbeersChunk] = useState([])
  const [filteredBy, setFilter] = useState('RATE_BEER')
  const [avg, setAvg] = useState({ price: 0, untappd: 0, rateBeer: 0 })
  const { price, untappd, rateBeer } = avg

  const [hasMore, SetHasMore] = useState(true)

  //For AVG
  useEffect(() => {
    if (loading === false && data) {
      setAllBeers(uniqBy(data.beers, 'name'))
    }
  }, [loading, data])

  useEffect(() => {
    if (loading === false && data) {
      setAllBeers(uniqBy(data.beers, 'name'))
      allBeers.sort()

      setbeersChunk(chunk(uniqBy(data.beers, 'name'), 20))
    }
  }, [loading, data, filteredBy])

  useEffect(() => {
    if (loading === false && beersChunk[0]) {
      setBeers(beersChunk[0])
    }
  }, [loading, beersChunk, filteredBy])

  // console.log('data', data)
  // console.log('beers', beers)
  // console.log('beersChunk', beersChunk)

  // Calculate AVG
  let len = allBeers.length

  let sumPrice = 0
  let sumRateBeer = 0
  let sumUntappd = 0
  let lenPrice = 0
  let lenUntapppd = 0
  let lenRateBeer = 0

  for (var i = 0; i < len; i++) {
    if (allBeers[i].price && allBeers[i].price !== '0') {
      sumPrice += parseFloat(allBeers[i].price)
      lenPrice += 1
    }

    if (allBeers[i].ratings[0] && allBeers[i].ratings[0].value !== '0') {
      sumUntappd += parseFloat(allBeers[i].ratings[0].value)
      lenUntapppd += 1
    }
    if (allBeers[i].ratings[1] && allBeers[i].ratings[1].value !== '0') {
      sumRateBeer += parseFloat(allBeers[i].ratings[1].value)
      lenRateBeer += 1
    }
  }

  useEffect(() => {
    if (sumUntappd !== 0 && sumPrice !== 0 && sumRateBeer !== 0) {
      setAvg({
        ...avg,
        price: (sumPrice / lenPrice).toFixed(2),
        untappd: (sumUntappd / lenUntapppd).toFixed(2),
        rateBeer: (sumRateBeer / lenRateBeer).toFixed(2),
      })
    }
  }, [allBeers])

  const sortByPrice = (a, b) => {
    let priceA = parseInt(a.price)
    let priceB = parseInt(b.price)

    if (priceA < priceB) {
      return -1
    }
    if (priceA > priceB) {
      return 1
    }
    return 0
  }

  const sortByUntappd = (a, b) => {
    let ratingA = parseFloat(a.ratings[0].value)
    let ratingB = parseFloat(b.ratings[0].value)

    return ratingB - ratingA
  }

  const sortByRatebeer = (a, b) => {
    let ratingA = 0
    let ratingB = 0
    if (a.ratings[1] && b.ratings[1]) {
      ratingA = parseFloat(a.ratings[1].value)
      ratingB = parseFloat(b.ratings[1].value)
    }

    return ratingB - ratingA
  }

  if (beers) {
    switch (filteredBy) {
      case 'PRICE':
        beers.sort(sortByPrice)
        break
      case 'UNTAPPD':
        beers.sort(sortByUntappd)
        break
      case 'RATE_BEER':
        beers.sort(sortByRatebeer)
        break
      default:
        break
    }
  }

  //Pagination
  let pn = 1
  const fetchMoreData = () => {
    if (pn >= beersChunk.length) {
      SetHasMore(false)
    }

    if (beersChunk[pn]) {
      setBeers(beers.concat(beersChunk[pn]))
    }
  }

  if (loading) return <h4>Loading...</h4>
  if (error) console.log(error)

  return (
    <div>
      <div className='cardy text-white bg-light mb-3 sticky-top pb-3'>
        <h1 className='display-4 my-3 filxed-top bg-light ml-4 '>
          Beers Quebec{' '}
        </h1>
        {/* <div className='card-header'></div> */}
        <div className='card-body'>
          {/* <h4 className='card-title'>Primary card title</h4> */}

          <form className='form-inline '>
            <button
              className={
                filteredBy === 'PRICE' ? 'btn btn-primary' : 'btn btn-secondary'
              }
              type='button'
              onClick={() => {
                beers.sort(sortByPrice)
                setBeers(beers)
                setFilter('PRICE')
                SetHasMore(true)
                window.scrollTo(0, 0)
              }}
            >
              By Price
            </button>
            <button
              className={
                filteredBy === 'UNTAPPD'
                  ? 'btn btn-primary '
                  : 'btn btn-secondary'
              }
              type='button'
              onClick={() => {
                beers.sort(sortByUntappd)
                setBeers(beers)
                SetHasMore(true)

                setFilter('UNTAPPD')
                window.scrollTo(0, 0)
              }}
            >
              By Untappd
            </button>
            <button
              className={
                filteredBy === 'RATE_BEER'
                  ? 'btn btn-primary'
                  : 'btn btn-secondary'
              }
              type='button'
              onClick={() => {
                beers.sort(sortByRatebeer)
                setBeers(beers)
                SetHasMore(true)

                setFilter('RATE_BEER')
                window.scrollTo(0, 0)
              }}
            >
              By Ratebeer{' '}
            </button>
          </form>
          <div className='avg sticky-top col-sm-4 mt-3'>
            <h5 className='avgPart  '>Avg. Price : $ {avg.price}</h5>
            <h5 className='  '> Avg. Untappd : {avg.untappd} / 5</h5>
            <h5 className='  '> Avg. Ratebeer : {avg.rateBeer} / 5</h5>
          </div>
        </div>
      </div>

      {/* <nav className='nav sticky-top col-sm-4'></nav> */}
      {beers ? (
        <InfiniteScroll
          dataLength={beers.length}
          next={fetchMoreData}
          hasMore={hasMore}
          loader={<h4>Loading...</h4>}
          endMessage={
            <p style={{ textAlign: 'center' }}>
              <b>Yay! You've seen all {allBeers.length} beers!</b>
            </p>
          }
        >
          {beers.map((beer, index) => {
            pn = parseInt(Math.ceil(beers.length / 20))

            return <BeerItem key={beer.name} beer={beer} />
          })}
        </InfiniteScroll>
      ) : null}
    </div>
  )
}

