const BEERS_QUERY = gql`
  query BeersQuery {
    beers {
      abv
      available
      brewery
      ibu
      link
      name
      price
      ratings {
        name
        value
      }
      style
    }
  }
`
const LINKS_PER_PAGE = 5

const Beers = () => {
  return (
    <Fragment>
      <h1 className='display-4 my-3'>Beers du Quebec </h1>

      <Query query={BEERS_QUERY}>
        {({ loading, error, data }) => {
          if (loading) return <h4>Loading...</h4>
          if (error) console.log(error)

          return (
            <Fragment>
              {data.beers.map((beer) => (
                <BeerItem
                  key={beer.name}
                  beer={beer}
                  forPafination={this._forPafination}
                />
              ))}
            </Fragment>
          )
        }}
      </Query>
    </Fragment>
  )
}

