import React, { Component, Fragment, useState, useEffect } from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import BeerItem from './BeerItem'
import axios from 'axios'
import Pagination from './Pagination'
import InfiniteScroll from 'react-infinite-scroll-component'
// import InfiniteScroll from 'react-infinite-scroller'

const BEERS_QUERY = gql`
  query BeersQuery {
    beers {
      abv
      available
      brewery
      ibu
      link
      name
      price
      ratings {
        name
        value
      }
      style
    }
  }
`

const style = {
  height: 30,
  border: '1px solid green',
  margin: 6,
  padding: 8,
}
let beers = []
let beersWithRatebeer = []

export const Beers = () => {
  // a.price > b.price ? 1 :

  const sortByPrice = (a, b) => {
    let priceA = parseInt(a.price)
    let priceB = parseInt(b.price)

    if (priceA < priceB) {
      return -1
    }
    if (priceA > priceB) {
      return 1
    }

    return 0
  }

  const sortByUntappd = (a, b) => {
    let ratingA = parseFloat(a.ratings[0].value)
    let ratingB = parseFloat(b.ratings[0].value)

    return ratingB - ratingA
  }

  const sortByRatebeer = (a, b) => {
    let ratingA = 0
    let ratingB = 0
    if (a.ratings[1] && b.ratings[1]) {
      ratingA = parseFloat(a.ratings[1].value)
      ratingB = parseFloat(b.ratings[1].value)
    } else {
    }

    return ratingB - ratingA
  }
  return (
    <Fragment>
      <h1 className='display-4 my-3'>Beers du Quebec </h1>

      <nav class='nav'>
        <form class='form-inline'>
          <button class='btn btn-primary' type='button'>
            By Price
          </button>
          <button
            class='btn btn-secondary'
            type='button'
            onClick={() => {
              beers.sort(sortByUntappd)
              console.log('adfadf')
            }}
          >
            By Untappd
          </button>
          <button class='btn btn-secondary' type='button'>
            By Ratebeer
          </button>
        </form>
      </nav>
      <Query query={BEERS_QUERY}>
        {({ loading, error, data, fetchMore }) => {
          if (loading) return <h4>Loading...</h4>
          if (error) console.log(error)

          beers = data.beers

          beersWithRatebeer = beers.filter(
            (beer) => beer.ratings[1] !== undefined
          )

          beersWithRatebeer.sort(sortByRatebeer)

          console.log(beersWithRatebeer)

          return (
            <Fragment>
              {beersWithRatebeer.map((beer) => (
                <BeerItem key={beer.name} beer={beer} />
              ))}
            </Fragment>
          )
        }}
      </Query>
    </Fragment>
  )
}

