  //     setPosts(res.data)
  //     setLoading(false)
  //   }

  //   fetchPosts()
  // }, [])

  // console.log(posts)

  // Get current posts
  const indexOfLastPost = currentPage * postsPerPage
  const indexOfFirstPost = indexOfLastPost - postsPerPage

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber)

  return (
    <Fragment>
      <h1 className='display-4 my-3'>Beers du Quebec </h1>

      <Query query={BEERS_QUERY}>
        {({ loading, error, data }) => {
          if (loading) return <h4>Loading...</h4>
          if (error) console.log(error)

          const currentPosts = data.beers.slice(
            indexOfFirstPost,
            indexOfLastPost
          )
          console.log(data.beers)
          setPosts(data.beers)
          return (
            <Fragment>
              {currentPosts.map((beer) => (
                <BeerItem key={beer.name} beer={beer} />
              ))}
              <Pagination
                postsPerPage={postsPerPage}
                totalPosts={posts.length}
                paginate={paginate}
              />
            </Fragment>
          )
        }}
      </Query>
    </Fragment>
  )
}

