import React, { Component, Fragment } from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import BeerItem from './BeerItem'

const BEERS_QUERY = gql`
  query BeersQuery($first: Int, $skip: Int, $orderBy: LinkOrderByInput) {
    beers(first: $first, skip: $skip, orderBy: $orderBy) {
      abv
      available
      brewery
      ibu
      link
      name
      price
      ratings {
        name
        value
      }
      style
    }
  }
`
const LINKS_PER_PAGE = 5

const beers =()=> {
  return (
    <Fragment>
      <h1 className='display-4 my-3'>Beers du Quebec </h1>

      <Query query={BEERS_QUERY} variables={this._getQueryVariables}>
        {({ loading, error, data }) => {
          if (loading) return <h4>Loading...</h4>
          if (error) console.log(error)

          return (
            <Fragment>
              {data.beers.map((beer) => (
                <BeerItem
                  key={beer.name}
                  beer={beer}
                  forPafination={this._forPafination}
                />
              ))}
            </Fragment>
          )
        }}
      </Query>
    </Fragment>
  )
}

export class Beers extends Component {
  _forPafination = (store) => {
    const isNewPage = this.props.location.pathname.includes('new')
    const page = parseInt(this.props.match.params.page, 10)

    const skip = isNewPage ? (page - 1) * LINKS_PER_PAGE : 0
    const first = isNewPage ? LINKS_PER_PAGE : 100
    const orderBy = isNewPage ? 'createdAt_DESC' : null

    const data = store.readQuery({
      query: BEERS_QUERY,
      variables: { first, skip, orderBy },
    })

    store.writeQuery({ query: BEERS_QUERY, data })
  }

  _getQueryVariables = () => {
    const page = parseInt(this.props.match.params.page, 10)

    const skip = (page - 1) * LINKS_PER_PAGE
    const first = LINKS_PER_PAGE
    const orderBy = 'createdAt_DESC'
    return { first, skip, orderBy }
  }

  render() {
    return (
      <Fragment>
        <h1 className='display-4 my-3'>Beers du Quebec </h1>

        <Query query={BEERS_QUERY} variables={this._getQueryVariables}>
          {({ loading, error, data }) => {
            if (loading) return <h4>Loading...</h4>
            if (error) console.log(error)

            return (
              <Fragment>
                {data.beers.map((beer) => (
                  <BeerItem
                    key={beer.name}
                    beer={beer}
                    forPafination={this._forPafination}
                  />
                ))}
              </Fragment>
            )
          }}
        </Query>
      </Fragment>
    )
  }
}

export default Beers
