import React, { Component, Fragment, useState, useEffect } from 'react'
import gql from 'graphql-tag'
import { Query } from 'react-apollo'
import { useQuery } from '@apollo/react-hooks'
import BeerItem from './BeerItem'
import axios from 'axios'
import Pagination from './Pagination'
import InfiniteScroll from 'react-infinite-scroll-component'
import uniqBy from 'lodash.uniqby'
// var uniqBy = require('lodash.uniqby')
// import InfiniteScroll from 'react-infinite-scroller'

const BEERS_QUERY = gql`
  query BeersQuery {
    beers {
      abv
      available
      brewery
      ibu
      link
      name
      price
      ratings {
        name
        value
      }
      style
    }
  }
`

export const Beers = () => {
  const { loading, error, data } = useQuery(BEERS_QUERY)

  const [beers, setBeers] = useState([])
  const [filteredBy, setFilter] = useState('PRICE')
  const [avg, setAvg] = useState({ price: 0, untappd: 0, rateBeer: 0 })

  const { price, untappd, rateBeer } = avg

  const sortByPrice = (a, b) => {
    let priceA = parseInt(a.price)
    let priceB = parseInt(b.price)

    if (priceA < priceB) {
      return -1
    }
    if (priceA > priceB) {
      return 1
    }
    return 0
  }

  const sortByUntappd = (a, b) => {
    let ratingA = parseFloat(a.ratings[0].value)
    let ratingB = parseFloat(b.ratings[0].value)

    return ratingB - ratingA
  }

  const sortByRatebeer = (a, b) => {
    let ratingA = 0
    let ratingB = 0
    if (a.ratings[1] && b.ratings[1]) {
      ratingA = parseFloat(a.ratings[1].value)
      ratingB = parseFloat(b.ratings[1].value)
    }

    return ratingB - ratingA
  }
  console.log(data)

  useEffect(() => {
    if (loading === false && data) {
      setBeers(uniqBy(data.beers, 'name'))
    }
  }, [loading, data])

  // Calculate AVG
  let len = beers.length
  let sumPrice = 0
  let sumRateBeer = 0
  let sumUntappd = 0
  let lenPrice = 0
  let lenUntapppd = 0
  let lenRateBeer = 0

  // let forUntappd = beers.filter((beer) => beer.ratings[0].value !== undefined)

  // console.log(forUntappd)

  for (var i = 0; i < len; i++) {
    if (beers[i].price && beers[i].price !== '0') {
      sumPrice += parseFloat(beers[i].price)
      lenPrice += 1
    }

    if (beers[i].ratings[0] && beers[i].ratings[0].value !== '0') {
      sumUntappd += parseFloat(beers[i].ratings[0].value)
      lenUntapppd += 1
    }
    if (beers[i].ratings[1] && beers[i].ratings[1].value !== '0') {
      sumRateBeer += parseFloat(beers[i].ratings[1].value)
      lenRateBeer += 1
    }
  }
  // console.log('sum', sum)
  // console.log(sumUntappd)

  // calAvg()

  useEffect(() => {
    if (sumUntappd !== 0 && sumPrice !== 0 && sumRateBeer !== 0) {
      setAvg({
        ...avg,
        price: (sumPrice / lenPrice).toFixed(2),
        untappd: (sumUntappd / lenUntapppd).toFixed(2),
        rateBeer: (sumRateBeer / lenRateBeer).toFixed(2),
      })
    }
    console.log(avg)
  }, [sumPrice, sumUntappd, sumRateBeer])

  console.log(avg)

  console.log(beers)

  switch (filteredBy) {
    case 'PRICE':
      beers.sort(sortByPrice)
      break
    case 'UNTAPPD':
      beers.sort(sortByUntappd)
      break
    case 'RATE_BEER':
      beers.sort(sortByRatebeer)
      break
    default:
      break
  }

  if (loading) return <h4>Loading...</h4>
  if (error) console.log(error)

  return (
    <div>
      <h1 className='display-4 my-3 filxed-top  '>Beers Quebec </h1>
      <div className='cardy text-white bg-default mb-3 sticky-top'>
        <div className='card-header'>AVG</div>
        <div className='card-body'>
          <h4 className='card-title'>Primary card title</h4>
          <p className='card-text'>
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </p>
          <form className='form-inline '>
            <button
              className={
                filteredBy === 'PRICE' ? 'btn btn-primary' : 'btn btn-secondary'
              }
              type='button'
              onClick={() => {
                beers.sort(sortByUntappd)
                setBeers(beers)
                setFilter('PRICE')
                window.scrollTo(0, 0)
              }}
            >
              By Price
            </button>
            <button
              className={
                filteredBy === 'UNTAPPD'
                  ? 'btn btn-primary '
                  : 'btn btn-secondary'
              }
              type='button'
              onClick={() => {
                beers.sort(sortByUntappd)
                setBeers(beers)
                setFilter('UNTAPPD')
                window.scrollTo(0, 0)
              }}
            >
              By Untappd
            </button>
            <button
              className={
                filteredBy === 'RATE_BEER'
                  ? 'btn btn-primary'
                  : 'btn btn-secondary'
              }
              type='button'
              onClick={() => {
                beers.sort(sortByUntappd)
                setBeers(beers)
                setFilter('RATE_BEER')
                window.scrollTo(0, 0)
              }}
            >
              By Ratebeer{' '}
            </button>
          </form>
          <div className='avg sticky-top col-sm-4'>
            <h4 className='  '>Avg. Price : $ {avg.price}</h4>
            <h4 className='  '> Avg. Untappd : {avg.untappd} / 5</h4>
            <h4 className='  '> Avg. on Ratebeer : {avg.rateBeer} / 5</h4>
          </div>
        </div>
      </div>

      <nav className='nav sticky-top col-sm-4'></nav>

      <Fragment className='col-sm-8'>
        {beers.map((beer) => (
          <BeerItem key={beer.name} beer={beer} />
        ))}
      </Fragment>
    </div>
  )
}

